            array('id' => '62', 'faction' => $faction4, 'map' => $map1, 'markerType' => $markerType3, 'name' => 'Carrefour de Ruel', 'description' => '', 'altitude' => '0', 'latitude' => '68.160659347746', 'longitude' => '-71.238281875849', 'createdAt' => '2015-06-06 23:48:08', 'updatedAt' => '2015-06-06 23:48:08', 'deletedAt' => null),
            array('id' => '63', 'faction' => $faction11, 'map' => $map1, 'markerType' => $markerType2, 'name' => 'Koskan', 'description' => 'Cité aux mille visages, Koskan cache de nombreux et sombres secrets... Son économie particulière repose sur les activités commerciales, notamment celles des chars à voile en provenance de Seòl et de Smaràg, et profite autant qu\'elle peut du trafic de fret... et de voyageur. En effet, corruption, criminalité et même esclavagisme font ici la loi, le seigneur Aliser Mac Geovran se souciant plus de ses affaires personnelles que de celles de sa ville.', 'altitude' => '0', 'latitude' => '53.783344888615', 'longitude' => '-70.535156875849', 'createdAt' => '2015-03-31 10:56:03', 'updatedAt' => '2015-06-06 23:48:51', 'deletedAt' => null),
