            array('id' => '77', 'faction' => $faction4, 'map' => $map1, 'markerType' => $markerType5, 'name' => 'Cap des Adieux', 'description' => '', 'altitude' => '0', 'latitude' => '26.954717805555', 'longitude' => '-157.63476625085', 'createdAt' => '2015-06-06 23:48:08', 'updatedAt' => '2015-06-06 23:48:08', 'deletedAt' => null),
            array('id' => '78', 'faction' => $faction5, 'map' => $map1, 'markerType' => $markerType2, 'name' => 'Iolach', 'description' => 'Ce port de pêche, le plus au sud de la péninsule, est pour certains un trou perdu, et un repère à vide-gousset pour les autres. Les ruines d\'un fortin des chevaliers ronces attestent de leur présence passée.', 'altitude' => '0', 'latitude' => '28.097828285526', 'longitude' => '-145.8164075017', 'createdAt' => '2015-03-30 11:43:28', 'updatedAt' => '2015-06-06 23:48:51', 'deletedAt' => null),
            array('id' => '79', 'faction' => $faction10, 'map' => $map1, 'markerType' => $markerType1, 'name' => 'Merieren', 'description' => 'Dominant le fleuve Klaedhin et située au carrefour entre la route des capitales et celle qui mène à Ostreach, Merieren est une place marchande où commencent les territoires marécageux des Ahman Glas, à l\'entrée du duché de Tuaille.', 'altitude' => '0', 'latitude' => '56.860985750645', 'longitude' => '-101.1181640625', 'createdAt' => '2015-03-13 18:59:11', 'updatedAt' => '2015-06-06 23:48:51', 'deletedAt' => null),
            array('id' => '80', 'faction' => $faction11, 'map' => $map1, 'markerType' => $markerType6, 'name' => 'Aelwyd Saogh', 'description' => 'L\'ancienne citadelle où les trois rois de la péninsule se réunissaient tous les dix ans est abandonnée depuis plus de cinq siècles. Les routes qui y mènent sont difficilement praticables, et seuls quelques rares fous osent s\'y rendre, pensant à tort y trouver quelque trésor.', 'altitude' => '0', 'latitude' => '71.436508079644', 'longitude' => '-85.771728828549', 'createdAt' => '2015-04-02 11:14:24', 'updatedAt' => '2015-06-06 23:48:50', 'deletedAt' => null),
            array('id' => '81', 'faction' => $faction4, 'map' => $map1, 'markerType' => $markerType5, 'name' => 'Aiguille Mòrail', 'description' => 'Le plus haut sommet de Taol-Kaer est surnommé ainsi en raison de sa silhouette effilée.', 'altitude' => null, 'latitude' => '0', 'longitude' => '0', 'createdAt' => '2015-03-27 11:41:04', 'updatedAt' => '2015-06-06 23:48:50', 'deletedAt' => null),
            array('id' => '82', 'faction' => $faction3, 'map' => $map1, 'markerType' => $markerType4, 'name' => 'Alignements de Tùrsal', 'description' => 'Le liagcal de l\'ouest, Tùrsal, est célèbre pour ses pierres levées, dressées en cercle mais aussi en longs alignements. Lieu sacré et craint, c\'est ici que les demorthèn de la région se rassemblent annuellement, l\'occasion pour les ionnthén d\'achever leur initiation. Les landes environnantes habituellement désertes se couvrent à l\'approche du Tsioghair d\'éphémères villages de tentes, pour abriter les demorthèn mais aussi l\'intendance et les pèlerins, les marchands et les voleurs, les gardes et les espions des ducs.', 'altitude' => null, 'latitude' => '0', 'longitude' => '0', 'createdAt' => '2015-03-27 11:47:28', 'updatedAt' => '2015-06-06 23:48:50', 'deletedAt' => null),
